:root{
    --colorOne: #D0EDF5;
    --colorTwo: #E2F0D6;
    --colorThree: #FCF8B9;
    --colorFour: #EBEBEB;
    --colorFive: #FDC3C2;
    --colorSix: #E2F0D6;
    --colorSeven: #FCF8B9;
}
.threeGrid{
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    margin-bottom: 70px;
}
.threeGrid h1{
    font-size: 80px;
    margin: 0px;
    justify-content: flex-start;
    align-items: flex-start;
}
.header__column{
    display: grid;
    grid-template-columns: 1fr 1fr;
    justify-content: flex-start;
}
.header__nav{
    list-style: none;
    padding-left: 0px;
}
.header__nav span{
    display: block;
    color: gray;
    text-decoration: underline;
    text-transform: uppercase;
    margin-bottom: 15px;
}
.grid{
    grid-auto-rows: 200px;
    grid-gap: 20px;
    margin-left: 40px;
    margin-right: 40px;
}
.grid__items{
    background-size: cover;
    background-position: center center;
    padding: 30px;
    padding-top: 50px;
    display: grid;
    align-items: flex-start;
}
.grid__items div:first-child{
    color: gray;
    text-decoration: underline;
}
.grid__items p{
    color: gray;
}
.grid__items div:last-child{
    align-self: flex-end;
    color: gray;
    text-decoration: underline;
}
.grid__items:first-child{
    background-color: var(--colorOne);
    grid-row: span 2;
}
.grid__items:nth-child(2){
    background-image: url(https://i.pinimg.com/564x/17/2b/f8/172bf8ac861d868baeb6113a3109d111.jpg);
    grid-area: span 2 / span 2;
}
.grid__items:nth-child(3){
    background-color: var(--colorTwo);
}
.grid__items:nth-child(4){
    background-image: url(https://i.pinimg.com/564x/91/95/36/919536365a6488de669974e843d693e1.jpg);
    grid-row: span 2;
}
.grid__items:nth-child(5){
    background-color: var(--colorThree);
}
.grid__items:nth-child(6){
    background-color: var(--colorFour);
}
.grid__items:nth-child(7){
    background-image: url(https://i.pinimg.com/564x/cc/83/8c/cc838cb425bc87f61392867688bce90c.jpg);
}
.grid__items:nth-child(8){
    background-color: var(--colorFive);
    grid-area: span 2 / span 2;
}
.grid__items:nth-child(9){
    background-image: url(https://i.pinimg.com/564x/1e/0a/b7/1e0ab71be57c78b4dbb52b8514fccb7f.jpg);
    grid-row: span 2;
}
.grid__items:nth-child(10){
    background-image: url(https://i.pinimg.com/564x/af/71/54/af71540333ff77edd43fc3be222f90a4.jpg);
    grid-area: span 2 / span 2;
}
.grid__items:nth-child(11){
    background-image: url(https://i.pinimg.com/564x/c0/d6/2e/c0d62ee8aa87de128214c0dd933d8d04.jpg);
}
.grid__items:nth-child(12){
    background-image: url(https://i.pinimg.com/564x/bc/2c/36/bc2c36715e72ce562c970532b13e9994.jpg);
}
.grid__items:nth-child(13){
    background-color: var(--colorSix);
    grid-area: span 2 / span 2;
}
.grid__items:nth-child(14){
    background-image: url(https://i.pinimg.com/564x/6e/f5/df/6ef5dfd4c597cbc8623429afd3a82220.jpg);
}
.grid__items:nth-child(15){
    background-color: var(--colorSeven);
}
.sixGrid{
    display: grid;
    grid-template-columns: repeat(6, 1fr);
}
.footer__column .footer__nav{
    list-style: none;
    color: #B5B1B1;
}
.footer__column:nth-child(1){
    grid-column: 2;
}
.footer__column:last-child{
    grid-column: -2;
}
